import React from 'react';
import Link from 'next/link';
import { Form, Menu, Input, Button, Row, Col, Card, Avatar } from 'antd';
import PropTypes from 'prop-types';

const dummy={
  nickname: 'joey',
  Post: [],
  Followings: [],
  Followers: [],
  isLoggedIn: false
};

const AppLayout = ( { children } ) => {
  return (
  <div>
    <Menu mode="horizontal">
      <Menu.Item key="home"><Link href="/"><a>StarLive</a></Link></Menu.Item>
      <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
      <Menu.Item key="mail">
        <Input.Search enterButton style={{verticalAlign: 'middle'}}/>
      </Menu.Item>
    </Menu>
    {/*xs:모바일, sm:작은화면, md: 중간화면, lg: 큰화면*/}
    <Row>
      <Col xs={24} md={6}>
      {/*로그인구현
        isLoggedIn값이 true이면 유저의 정보가 보이고, false이면 로그인창이 보이는 삼항연산자*/}
       {dummy.isLoggedIn
         ? <Card
         actions={[
           <div key="twit">짹짹<br />{dummy.Post.length}</div>,
           <div key="twit">팔로잉<br />{dummy.Followings.length}</div>,
           <div key="twit">팔로워<br />{dummy.Followers.length}</div>,
         ]}>
          <Card.Meta
            avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
            title={dummy.nickname}
          />
        </Card>
        :
        <LoginForm /> }

      </Col>
      <Col xs={24} md={12}>
      </Col>
      <Col xs={24} md={6}>
      </Col>
    </Row>


    {children}
  </div>
  );
};
export default AppLayout;
