import React from 'react';
import Link from 'next/link';
import { Form, Menu, Input, Button, Row, Col, Card, Avatar } from 'antd';
import PropTypes from 'prop-types';
import LoginForm from './LoginForm';
import UserProfile from './UserProfile';


{/*js파일에 중복사용되는 레이아웃을 모아놓은 파일*/}
const dummy={
  nickname: 'Joey',
  Post: [],
  Followings: [],
  Followers: [],
  isLoggedIn: true,
};

const AppLayout = ( { children } ) => {
  return (
  <div>
    <Menu mode="horizontal">
      <Menu.Item key="home"><Link href="/"><a>StarLive</a></Link></Menu.Item>
      <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>
      <Menu.Item key="mail">
        <Input.Search enterButton style={{verticalAlign: 'middle'}}/>
      </Menu.Item>
    </Menu>
    {/*xs:모바일, sm:작은화면, md: 중간화면, lg: 큰화면*/}
    <Row gutter={8}>
      <Col xs={24} md={6}>
      {/*로그인구현
        isLoggedIn값이 true이면 유저의 정보가 보이고, false이면 로그인창이 보이는 삼항연산자*/}
        {dummy.isLoggedIn
             ? <UserProfile />
             : <LoginForm />}
      </Col>
      <Col xs={24} md={12}>
        {children}
      </Col>
      <Col xs={24} md={6}>
        <Link href="" ><a target="_blank">Made by Joey!</a></Link>
      </Col>
    </Row>
  </div>
  );
};

AppLayout.propTypes = {
  children: PropTypes.node,
};

export default AppLayout;
